INSERT INTO `runes` VALUES (1,'死亡電刑',1,'<font color=\"#e0ce2b\">爆發傷害</font><br>/<b>於3秒內使用3次普攻或技能命中一名敵方英雄時，將造成額外適性傷害。</b><br>傷害值：<b>30~184(隨等級提升)</b>+<font color=\"orange\">40%額外AD</font>+<font color=\"blue\">25%AP的適性傷害</font><br>冷卻時間：<b>25秒</b>','https://lolwildriftbuild.com/wp-content/uploads/2020/10/electrocute_wild_rift.png'),(2,'艾莉',1,'<font color=\"#e0ce2b\">打帶跑、保護</font><br>/<b>普攻或技能會將艾莉送至木腰身上；目標為敵軍則造成傷害，為友軍護盾。</b><br>傷害：<b>10~60(隨等級上升)</b>+<font color=\"orange\">20%額外AD</font>+<font color=\"blue\">10%AP的傷害</font><br>護盾：<b>20~120(隨等級上升)</b>+<font color=\"orange\">40%額外AD</font>+<font color=\"blue\">20%AP的護盾</font><br>艾莉回到身邊後才可再次送至新的目標。','https://lolwildriftbuild.com/wp-content/uploads/2020/10/aery_wild_rift.png'),(3,'征服者',1,'<font color=\"#e0ce2b\">傷害疊加</font><br>/<b>使用獨立的普攻或技能命中一名英雄時，可疊加適性之力，最多可疊加5次，疊滿時度英雄造成額外的適性傷害。</b>每層：增加<font color=\"orange\">2~6額外物理傷害</font>或<font color=\"blue\">3~9魔法傷害</font>，<b>持續8秒</b><br>疊滿加成：近戰：10%。遠程：對英雄造成<b>7%</b>額外<font color=\"pink\">適性傷害</font>','https://lolwildriftbuild.com/wp-content/uploads/2020/10/conqueror_wild_rift.png'),(4,'瞬疾步法',1,'<font color=\"#e0ce2b\">機動性、治癒</font><br>/<b>移動、普攻、施放技能疊加能量，累積至100層時，下一次普攻會治癒自己並增加跑速。</b><br><font color=\"green\">治癒量</font>：<b>15~85(隨等級提升)</b>+<font color=\"orange\">額外30%AD</font>+<font color=\"blue\">30%AP</font><br><b>跑速</b>加成：<b>20%</b>，<b>持續1秒</b>。<br>若裝備在遠程英雄上，則普攻士兵時的治癒量改為<b>30%</b>。','https://lolwildriftbuild.com/wp-content/uploads/2020/10/fleetfootwork_wild_rift.png'),(5,'不死之握',1,'<font color=\"#e0ce2b\">坦克、治癒</font><br>/<b>戰鬥中每隔4秒，對英雄的下一次普功會根據自身最大生命值造成額外傷害。</b><br><font color=\"green\">治癒</font>：<font color=\"green\">治癒量為2%</font>自身最大生命<br>永久增加5最大生命<br>額外傷害：自身最大生命值的<font color=\"#4ebce7\">4%魔法傷害</font>若為遠距英雄，效果將減少<b>40%</b>','https://lolwildriftbuild.com/wp-content/uploads/2020/10/graspoftheundying_wild_rift.png'),(6,'裂地衝擊',1,'<font color=\"#e0ce2b\">控場、進攻</font><br>/<b>定身一名敵方英雄後可增加防禦，隨後在身後釋放衝擊波，對鄰近敵人造成魔法傷害。</b><br>防禦：<font color=\"#e0ce2b\">35物理防禦</font>+<font color=\"#e0ce2b\">額外60%物理防禦</font>以及<font color=\"#4ebce7\">35點魔法防禦+<font color=\"#4ebce7\">額外60%魔防</font>，持續2.5秒。<br>傷害：<b>12~110(隨等級提升)</b>+<font color=\"green\">3%最大生命</font>及<font color=\"#4ebce7\">魔法攻擊</font>','https://lolwildriftbuild.com/wp-content/uploads/2020/10/aftershock_wild_rift.pnghttps://lolwildriftbuild.com/wp-content/uploads/2020/10/aftershock_wild_rift.png'),(7,'生命之泉',1,'<font color=\"#e0ce2b\">團隊治療</font><br>/<b>對一名敵方英雄造成行動受限時，對其進行標記，友方英雄攻擊受被標記的敵人時，將依據最大生命獲得治癒</b><br><font color=\"green\">治癒量</font>：相當於<font color=\"green\">3%</font>你的最大<font color=\"green\">生命值</font>，回復需時<b>2秒</b>。<br>標記持續時間<b>4秒</b>','https://lolwildriftbuild.com/wp-content/uploads/2020/10/fontoflife_wild_rift.png'),(8,'竊盜高手',1,'<font color=\"#e0ce2b\">隨機增益</font><br>/<b>在施放技能後，使用普攻或技能命中時，可獲得隨機道具效果。</b><br>冷卻時間：<b>25秒</b>','https://lolwildriftbuild.com/wp-content/uploads/2020/10/kleptomancy_wild_rift.png'),(9,'兇蠻',2,'<font color=\"#e0ce2b\">物理/魔法攻擊、穿透</font><br>/增加<font color=\"orange\">7物理傷害</font>和<font color=\"#f88d5b\">2%物理穿透</font>，或<font color=\"blue\">14魔法傷害</font>及<font color=\"purple\">2%魔法穿透</font>。(<font color=\"#e798ff\">適性</font>)','https://lolwildriftbuild.com/wp-content/uploads/2020/10/brutal_wild_rift.png'),(10,'天寒地凍',2,'<font color=\"#e0ce2b\">增加物理/魔法攻擊</font><br>/每2分鐘獲得<font color=\"orange\">2物理傷害</font>或<font color=\"blue\">4魔法傷害</font>。(<font color=\"#e798ff\">適性</font>)','https://lolwildriftbuild.com/wp-content/uploads/2020/10/gatheringstorm_wild_rift.png'),(11,'嗜血獵人',2,'<font color=\"#e0ce2b\">物理/魔法攻擊、吸血</font><br>/獲得<font color=\"red\">2%物理吸血或<font color=\"pink\">2%魔法吸血</font>，擊殺或助攻獲得<font color=\"orange\">2物理傷害</font>+<font color=\"red\">1%物理吸血，或<font color=\"blue\">4魔法傷害</font>+<font color=\"pink\">1%魔法吸血</font>。(<font color=\"#e798ff\">適性</font>)(對同一英雄只會獲得1次)','https://lolwildriftbuild.com/wp-content/uploads/2020/10/huntervampirism_wild_rift.png'),(12,'凱旋',2,'<font color=\"#e0ce2b\">擊殺、治癒</font><br>/每次成功擊殺或助攻敵方英雄時，回復<font color=\"green\">10%以損失的生命值</font>。對低於<b>35%生命</b>的敵軍<b>造成3%額外傷害</b>。','https://lolwildriftbuild.com/wp-content/uploads/2020/10/triumph_wild_rift.png'),(13,'落井下石',2,'<font color=\"#e0ce2b\">控場、額外傷害</font><br>/每次成功擊殺或助攻敵方英雄時，回復<font color=\"green\">10%以損失的生命值</font>。對低於<b>35%生命</b>的敵軍<b>造成3%額外傷害</b>。對一名敵方英雄造成緩速或行動受限時標記對方5秒，受標記的目標受到的傷害提高5%','https://lolwildriftbuild.com/wp-content/uploads/2020/10/weakness_wild_rift.png'),(14,'英雄',2,'<font color=\"#e0ce2b\">高風險、高傷害</font><br>/對英雄造成<b>10%額外傷害</b>，每次自身陣亡將<b>減少5%效果</b>，直到完全扣除。','https://lolwildriftbuild.com/wp-content/uploads/2020/10/champion_wild_rift.png'),(15,'強健體魄',3,'<font color=\"#e0ce2b\">物理/魔法防禦</font><br>/增加<font color=\"#e0ce2b\">10物理防禦</font>或<font color=\"blue\">10魔法防禦</font>，取決於哪一個能力值較低。','https://lolwildriftbuild.com/wp-content/uploads/2020/10/backbone_wild_rift.png'),(16,'調節',3,'<font color=\"#e0ce2b\">中期增加物防/魔防</font><br>/對戰開始5分鐘後，增加<b>8額外<font color=\"#e0ce2b\">物理防禦</font>和<font color=\"blue\">魔法防禦</font>。','https://lolwildriftbuild.com/wp-content/uploads/2020/10/conditioning_wild_rift.png'),(17,'泰坦獵人',3,'<font color=\"#e0ce2b\">生命、韌性</font><br>/增加<font color=\"green\">20最大生命</font>。對每位敵方英雄的首次擊殺或助攻，將賦予自身<font color=\"green\">20最大生命</font>及<font color=\"purple\">4%韌性</font>。','https://lolwildriftbuild.com/wp-content/uploads/2020/10/huntertitan_wild_rift.png'),(18,'回復',3,'<font color=\"#e0ce2b\">回復生命/魔力</font><br>/每過3秒，將恢復<b>2%失去的</b><font color=\"green\">生命值</font>或<font color=\"blue\">魔力</font>，取決於何者的百分比低。','https://lolwildriftbuild.com/wp-content/uploads/2020/10/regeneration_wild_rift.png'),(19,'熱誠忠心',3,'<font color=\"#e0ce2b\">共同增加物防/魔防</font><br>/自身獲得<font color=\"#e0ce2b\">2物理防禦</font>與<font color=\"blue\">5魔法防禦</font>。最靠近的友方英雄獲得<font color=\"#e0ce2b\">5物理防禦</font>與<font color=\"#e0ce2b\">2魔法防禦</font>。','https://lolwildriftbuild.com/wp-content/uploads/2020/10/loyalty_wild_royalty.png'),(20,'神靈行者',3,'<font color=\"#e0ce2b\">生命值、緩速抗性</font><br>/增加<font color=\"green\">50最大生命</font>與<font color=\"pink\">20%緩速抗性</font>。','https://lolwildriftbuild.com/wp-content/uploads/2020/10/spiritwalker_wild_rft.png'),(21,'尋路者',4,'<font color=\"#e0ce2b\">移動</font><br>/脫離戰鬥時，在草叢、野區與河道中可增加<b>8%跑速</b>。','https://lolwildriftbuild.com/wp-content/uploads/2020/11/pathfinder_wild_rift.png'),(22,'首腦',4,'<font color=\"#e0ce2b\">目標物、金幣</font><br>/對史詩野怪及防禦塔造成<font color=\"#bbeeff\">10%額外的真實傷害</font>。每擊殺或助攻史詩級野怪/防禦塔，將獲得<font color=\"#e0ce2b\">100枚金幣</font>及<font color=\"pink\">500經驗值</font>。','https://lolwildriftbuild.com/wp-content/uploads/2020/11/mastermind_wild_rift.png'),(23,'靈巧獵人',4,'<font color=\"#e0ce2b\">冷卻時間減免</font><br>/獲得2.5%技能加速。對每位敵方英雄的首次擊殺或助攻，將賦予自身<b>2.5技能加速</b>。','https://lolwildriftbuild.com/wp-content/uploads/2020/11/huntergenius_wild_rift.png'),(24,'嗜甜如命',4,'<font color=\"#e0ce2b\">蜂蜜漿果、金幣</font><br>/食用<b>蜂蜜漿果</b>時的治癒效果提升<font color=\"green\">25%</font>，每個漿果還會給予<font color=\"#e0ce2b\">20枚金幣</font>。','https://lolwildriftbuild.com/wp-content/uploads/2020/11/sweettooth_wild_rift.png'),(25,'圍剿獵殺',4,'<font color=\"#e0ce2b\">移動、團隊經濟</font><br>/接近友方英雄時增加獲得<b>2%跑速</b>。你於美味有方英雄首次合立擊殺敵方英雄時，你會獲得<font color=\"#e0ce2b\">50枚金幣</font>，友方英雄也會獲得<font color=\"#e0ce2b\">50枚金幣</font>。','https://lolwildriftbuild.com/wp-content/uploads/2020/11/packhunter_wild_rift.png'),(26,'附魔之帶',4,'<font color=\"#e0ce2b\">魔力</font><br>/使用技能或<font color=\"green\">附帶增益效果</font>的普攻命中對方英雄時，使自身<font color=\"green\">最大魔力</font>永久增加<font color=\"green\">30</font>，最多提升<font color=\"green\">300</font>魔力。','https://lolwildriftbuild.com/wp-content/uploads/2020/11/manaflowband_wild_rift.png');
